import React, { useEffect, useState } from 'react'
import YouTube from 'react-youtube'
import axios from '../../axios'
import './RowPoster.css'
import { imgeUrl } from '../../constansts/constants'

// props for each row component, which contain title and url
function RowPoster(props) {
  const [movies, setMovies] = useState([])
  useEffect(() => {
    axios.get(props.url).then((response) => {
      setMovies(response.data.results)
    }).catch((err) => {
      alert('Network error. Please try again!')
    })
  },[])
  const handleClick = (id) => {
    alert('Please click on "Play" button to watch the trailer')
  }
  return (
      <div className='row_poster'>
        <h3>{props.title}</h3> 
        <div className="posters">
           {movies.map((movie) => (
            // Using dynamic class name
            <img onClick={() => props.handleClick(movie)} className={ props.isSmall ? 'small-poster' : 'poster'} src={ `${imgeUrl}${movie.poster_path}: 'Loading'}`} alt={movie.name} />
          ))}
        </div>
        <YouTube videoId="2g811Eo7K8U" opts={{height: '390', width: '100%', playerVars: {autoplay: 0}}} />
      </div>
  )
}

export default RowPoster
